import React, { Component, } from 'react';
import {Container,Col,Image} from 'react-bootstrap';
import descarga from './assets/descarga.png';
import Salon from './assets/Salon.jpg'
import "./Galeria.css";
class Galeria extends Component{
  constructor(props){
    super(props);
    this.state={
      items:[{descarga},{Salon}],
      viewImg:false,
      visibility: 'hidden'

    }
      this.handleClick = this.handleClick.bind(this);

  }
  handleClick() {
    if (!this.state.viewImg) {
      this.setState({viewImg:true,visibility: 'visible'})
    }
    if(this.state.viewImg){
      this.setState({viewImg:false,visibility: 'hidden'})
    }


  }
  itemsGaleria(props){
    const itemsImg = props.items;
    const listImg = itemsImg.map((item)=>(
      <button onClick={this.handleClick} key={item.toString()}>
          <Image src={item} thumbnail></Image>
      </button>

    )
  )
  return {listImg};
}


   render(){

     return (
       <div className="Galeria">
         <div style={this.state} className="contenedor-img">
           <div style={this.state} className="img-view">
             <Image src={Salon}  className="img-content" thumbnail></Image>
             <div><button onClick={this.handleClick} className="img-close">X</button></div>
           </div>
         </div>
       <Container>
         <itemsGaleria items={this.state.items}/>
        
       </Container>

     </div>
   )
   }
}
export default Galeria;
