import React, { Component, } from 'react';
import Galeria from './Galeria.js';
import {BrowserRouter as Router,Route,Switch,Link} from 'react-router-dom';
import {Container,Image} from 'react-bootstrap';
import descarga from './assets/descarga.png';
import Salon from './assets/Salon.jpg'
import "./Galeria.css";
class SelectGaleria extends Component{
  constructor(props){
    super(props);
    this.state={
      albums:{
        albumone:{
          name:'Bodas',
          photo:'',
          to:'/boda'
      },
      albumtwo:{
        name:'XVs',
        photo:'',
        to:'/xv'

      }}
    }
  }
  render(){
    function ViewAlbums (props){
      const albums = this.props.albums;
      const lstAlbums = albums.map((album,i)=>(
        <Link to={album.to}>
        <div className="responsive">
          <div className="album">
            <Image src={album.photo}></Image>
            <div className="titleAlbum">
              {album.name}
            </div>
          </div>
        </div>
        <div>
          <Route path={album.to} component="Galeria_page"/>
        </div>
        </Link>


      ))
      return (<Container>{lstAlbums}</Container>)
}
    return (
      <Router>

        <ViewAlbums albums={this.state.items.albums}></ViewAlbums>

      </Router>
    )
  }
}
export default SelectGaleria;
