import React, { Component, } from 'react';
import {Container,Col,Image} from 'react-bootstrap';
import descarga from './assets/descarga.png';
import Salon from './assets/Salon.jpg'
import "./Galeria.css";
class Galeria extends Component{
  constructor(props){
    super(props);
    this.state={
      items:[],
      viewImg:false,
      visibility: 'hidden'

    }
      this.handleClick = this.handleClick.bind(this);

  }
  handleClick() {
    if (!this.state.viewImg) {
      this.setState({viewImg:true,visibility: 'visible'})
    }
    if(this.state.viewImg){
      this.setState({viewImg:false,visibility: 'hidden'})
    }


  }


   render(){

     return (
       <div className="Galeria">
         <div style={this.state} className="contenedor-img">
           <div style={this.state} className="img-view">
             <Image src={descarga}  className="img-content" thumbnail></Image>
             <div><button onClick={this.handleClick} className="img-close">X</button></div>
           </div>
         </div>
       <Container>
          <button onClick={this.handleClick}>
              <Image src={descarga} thumbnail></Image>
          </button>
          <button onClick={this.handleClick}>
              <Image src={descarga} thumbnail></Image>
          </button>
       </Container>

     </div>
   )
   }
}
export default Galeria;
