import React, { Component } from 'react';
import { Navbar,Nav,Image} from 'react-bootstrap';
import Routing from './Routing.js';
import {BrowserRouter as Router,Route,Switch} from 'react-router-dom';
import logo1 from './assets/logo1.jpg';
import Home from './Home.js';
import Loader from './Loader.js';

import './App.css';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      loading : false
    }

  }


  componentDidMount(){
        changeState().then(()=>{
          this.setState({loading:true})
        });

  }

  render() {
      if(!this.state.loading){
        return(
          <Loader type="spin" color="#2196F3" />


        )
      }else {
        return (
        <Router>
        <div className="App">
          <div className="App-header">
          <Navbar  fixed="top" bg="light" expand="md" >
            <Navbar.Brand href="home" className="title-nav"><Image src={logo1} width="200px"/></Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto ">
                <Nav.Link href="home">Inicio</Nav.Link>
                <Nav.Link href="Galeria">Galeria</Nav.Link>
                <Nav.Link href="Paquetes">Servicios</Nav.Link>
                <Nav.Link href="Contacto">Contacto</Nav.Link>
                <Nav.Link href="Acerca">Nosotros</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
          </div>
          <div className="App-body">
            <Switch location={this.props.location}>
              <redirect from="/"  component={Home_page}/>
              <Routing/>
            </Switch>

          </div>
        </div>

        </Router>
      )
      }

  }
}

const changeState = () =>{
  return new Promise((resolve)=>{
    setTimeout(resolve,1000)
  })
}
const Home_page = ({match}) => (
  <Home/>
)

export default App;
